          height="1000px"
          image={Images[0].ImageSrc}
          pitch={10}
          yaw={180}
          hfov={110}
          autoLoad
          onLoad={() => {
              console.log("panorama loaded");
          }}
      >
      <Pannellum.Hotspot
        type="custom"
        pitch={31}
        yaw={150}
        handleClick={(evt, args) => this.hanldeClickImage(evt, args)}
        handleClickArg={{ name: "test" }}
      />
