import React, { Component, Button } from 'react';
import { render } from 'react-dom';
import { Pannellum } from "pannellum-react";
import { Images } from "./CairoPanoConfig.json";
import * as _ from 'underscore';

function PannellumReact(props)
{
  const [id, setId] = React.useState(0);
  const [yaw, setYaw] = React.useState(180);
  const [pitch, setPitch] = React.useState(10);

  function renderHotspots()
  {
    let info: Array<any> = [];
    for (var i = 0; i < Images.length; i++) 
    {
      if(Images.id === id)
      {
        info.push(Images[i].Hotspots);
      }
    }
    //console.log(info);

    //console.log("PATH");
    //console.log(info[0][0].Path);

    return info;
  };

  function handleClickHotspot(path, pitch, yaw)
  {
    console.log("Change Pano Button Clicked");
    //console.log(id);

    setYaw(yaw);
    setPitch(pitch);

    setId(path);
    console.log(path);
  };

  return(
    <div>
      <Pannellum
        width="100%"
        height="1100px"
        image={Images[id].ImageSrc}
        pitch={pitch}
        yaw={yaw}
        hfov={110}
        autoLoad
        onLoad={() => {
          console.log("panorama loaded");
        }}
      >
        {renderHotspots().length > 0 &&
          renderHotspots().map((value, index) => {
            return (
              <Pannellum.Hotspot
                type="custom"
                pitch={0}
                yaw={0}
                handleClick={() => handleClickHotspot(0, 0, 0)}
              />
            );
         })}
      </Pannellum>
    </div>
  )
}

export default PannellumReact;
