  {
    super(props);
    this.state = { id: 0 };
  }*/

  function useEffect()
  {
      console.log(`${Images[this.state.id].ImageSrc}`);
  };

  function handleClickHotspot(evt, args)
  {
    console.log(args.name);
    console.log("handleClickImage")
    console.log(this.state.id)
    this.setState({id: (this.id + 1)});
  };


  return(
    <div>
      <Pannellum
        width="100%"
        height="1000px"
        image={Images[this.state.id].ImageSrc}
        pitch={10}
        yaw={180}
        hfov={110}
        autoLoad
        onLoad={() => {
          console.log("panorama loaded");
        }}
      >
        <Pannellum.Hotspot
          type="custom"
          pitch={0}
          yaw={0}
          handleClick={(evt, args) => this.handleClickHotspot(evt, args)}
        />
      </Pannellum>
    </div>
  )
}

export default PannellumReact;
