          yaw={180}
          hfov={110}
          autoLoad
          onLoad={() => {
            console.log("panorama loaded");
          }}
        >
          <Pannellum.Hotspot
            type="custom"
            pitch={0}
            yaw={0}
            handleClick={(evt, args) => this.handleClickHotspot(evt, args)}
          />
        </Pannellum>
      </div>
    )
  }
}

export default PannellumReact;
