import React, { useEffect, useRef } from "react";
import L from "leaflet";
import * as esri from 'esri-leaflet';
import * as ELG from "esri-leaflet-geocoder";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'

import "leaflet/dist/leaflet.css";
import "esri-leaflet-geocoder/dist/esri-leaflet-geocoder.css";

import "leaflet/dist/leaflet.js";
import "esri-leaflet-geocoder/dist/esri-leaflet-geocoder.js";

// import marker icons
delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://unpkg.com/leaflet@1.4.0/dist/images/marker-icon-2x.png",
  iconUrl: "https://unpkg.com/leaflet@1.4.0/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.4.0/dist/images/marker-shadow.png"
});

function MapComp({center, zoom, height, geolocate, searchPlaces, marginClass, paddingClass, scrollWheelZoom, Dalton}) {

    const _height = height || "100vh";
    const _center = center || [0, 0];
    const _zoom = zoom || "3";
    const leafletMap = useRef(null);
    let LayersGroups = null;
    let map = null;

    const addMarkertoMap = (latlng, draggable, textLocate) => {
        LayersGroups.clearLayers();
        LayersGroups.addLayer(L.marker(latlng, {draggable}).bindPopup(textLocate).openPopup());
        map.setView(latlng);
    }


    const handleSearchResults = (searchControl) => {
        searchControl.on("results", function(data) {
            for (let i = data.results.length - 1; i >= 0; i--) {
                addMarkertoMap(data.results[i].latlng, true, data.text);
            }
        });
    }

    const onLocationFound = (e) => {
        addMarkertoMap(e.latlng, true);
    }

    function onLocationError(e) {
        if (geolocate) {
            alert(e.message);
        }

    }

    const DisableScrollWheelZoom = () => {
        if (!scrollWheelZoom) {
            map.scrollWheelZoom.disable();
            map.on('click', function() {
                map.scrollWheelZoom.enable();
            });
            map.on('mouseout', function() { 
                map.scrollWheelZoom.disable(); 
            });
        }
    }

    const handlesMap= () => {
        map.on('locationfound', onLocationFound);
        map.on('locationerror', onLocationError);
        DisableScrollWheelZoom();
    }

    useEffect(() => {
        map = leafletMap.current.leafletElement;
        esri.basemapLayer("DarkGray", { maxNativeZoom: 19}).addTo(map);

        if (map) {

            LayersGroups = new L.LayerGroup().addTo(map);

            if (geolocate) {
                map.locate({watch: true, enableHighAccuracy: true});
                handlesMap();
            }

            if (searchPlaces) {
                const searchControl = new ELG.Geosearch().addTo(map);
                handleSearchResults(searchControl);
            }

            if (Dalton) {
                L.ViewerControl = L.Control.extend({
                    options: {
                    position: 'topleft',
                    callback: null,
                    kind: '',
                    html: '⚙'
                    },
                    // when the control is added to the map, wire up its DOM dynamically and add a click listener
                    onAdd: function (map) {
                    var container = L.DomUtil.create('div', 'leaflet-control leaflet-bar');
                    var link = L.DomUtil.create('a', '', container);
                    link.href = '#';
                    link.title = 'View the 360 Viewer';
                    link.innerHTML = this.options.html;
                    L.DomEvent
                        .on(link, 'click', function () 
                        {
                        var zoom = map.getZoom();

                        //console.log("Max Zoom: " + map.getMaxZoom())
                        console.log("Current Zoom: " + zoom)

                        if(zoom > map.getMaxZoom() - 2)
                        {
                            sessionStorage.setItem('CacheLat', map.getCenter().lat)
                            sessionStorage.setItem('CacheLng', map.getCenter().lng)
                            sessionStorage.setItem('CacheZoom', zoom)
                            console.log("Lat: " + map.getCenter().lat)
                            console.log("Lng: " + map.getCenter().lng)
                            console.log("Zoom: " + zoom)

                            //window.location.href = "/360Viewer.html";                    
                        }
                        //end
                        }, this);
                    return container;
                    }
                });
                map.addControl(new L.ViewerControl());
            }
            }
        }, [])

    return (
        <Map
            className={`${marginClass|| 'm-0'} ${paddingClass|| 'p-0'}`}
            style={{height:_height}}
            center={_center}
            zoom= {_zoom} 
            ref={m => {
                leafletMap.current = m;
            }}
        >
            {/* <TileLayer
                attribution="&copy; <a href='https://osm.org/copyright'>OpenStreetMap</a> contributors"
                url={"https://tiles.stadiamaps.com/tiles/alidade_smooth_dark/{z}/{x}/{y}{r}.png"}
                maxZoom="20"
            /> */}
            <div className="pointer" />
        </Map>
    );
}

export default MapComp;
