    this.setState({id: (this.id + 1)});
  };

  render() {
    let imageEffect = Images[this.state.id].ImageSrc;

    console.log("imageEffect");
    console.log(this.imageEffect);

    return(
      <div>
        <Pannellum
            width="100%"
            height="1000px"
            image={this.imageEffect}
            pitch={10}
            yaw={180}
            hfov={110}
            autoLoad
            onLoad={() => {
                console.log("panorama loaded");
            }}
        >
        <Pannellum.Hotspot
          type="custom"
          pitch={0}
